void Pack_shuffle(Pack *pack_ptr)
{
Pack holder;
int j = 0;
const int SHUFFLER = 3;
const int CUT = 17;
for (int i = 0; i < SHUFFLER; i++)
{
for (int i = CUT; i < PACK_SIZE; ++i)
{
holder.cards[j] = pack_ptr->cards[i];
j++;
}
for (int i = 0; i < CUT; ++i)
{
holder.cards[j] = pack_ptr->cards[i];
j++;
}
Pack_reset(pack_ptr);
}
(*pack_ptr) = holder;
return;
}

