for (int i = 0; i < MAX_HAND_SIZE; i++){
if(player_ptr->hand[i].suit == led_card->suit){
canFollowSuit = true;
}
}

if (canFollowSuit == true){
Card *highest = &player_ptr->hand[0];
for (int i = 0; i < MAX_HAND_SIZE; i++){
if ( player_ptr->hand[i].suit == led_card->suit){
highest = &player_ptr->hand[i];
for ( int j = i + 1; j < MAX_HAND_SIZE; j++){
if ( player_ptr->hand[j].suit == led_card->suit){
int comparison = Card_compare(highest, &player_ptr->hand[j], led_card, trump);
if ( comparison < 0) highest = &player_ptr->hand[j];
}
}
remove_Card(player_ptr, highest);
return *highest;
}
}
remove_Card(player_ptr, highest);
return *highest;
}
else {
Card *lowest = &player_ptr->hand[0];
for (int i = 0; i < MAX_HAND_SIZE - 1 ; i++){
int compare = Card_compare(lowest, &player_ptr->hand[i+1], led_card, trump);
if (compare > 0) lowest = &player_ptr->hand[i+1];
}
remove_Card(player_ptr, lowest);
return *lowest;
}
}

