void Card_init(Card *card_ptr, const char* rank, const char* suit) {
Rank Ranks[] = {TWO, THREE, FOUR, FIVE, SIX, SEVEN, EIGHT, NINE,
TEN, JACK, QUEEN, KING, ACE};
Suit Suits[] = {SPADES, HEARTS, CLUBS, DIAMONDS};
Rank RANK = TWO;
Suit SUIT = SPADES;

for (int i = 0; i < RANK_SIZE; ++i) {
if (rank == RANK_NAMES[i]) {
RANK = Ranks[i];
}
}
for (int i = 0; i < SUIT_SIZE; ++i) {
if (suit == SUIT_NAMES[i]) {
SUIT = Suits[i];
}
}
Card_init(card_ptr, RANK, SUIT);
return;
}

