void ArrayIntVector::push_back(int value) {
check_invariants(); if (numElements == this->dataCapacity)
this->grow();
data[numElements++] = value; check_invariants();
}

void ArrayIntVector::grow() {
dataCapacity *= 2;
int *array = new int[dataCapacity];
for (int i = 0; i < dataCapacity / 2; i++)
{
*array = *this->data;
array++;
data++;
}
this->data = array;
delete[] array; array = 0;
check_invariants();
cout << "grow() was called. Capacity is now " << dataCapacity << "." << endl;
}

