TEMPLATED T LIST_METHOD pluck_node(Node* lhs, Node* rhs) {
Node* np = (lhs!=NULL ? lhs->next : rhs!=NULL ? rhs->prev : first); // find node to pluck,
link_nodes(lhs, rhs); // being careful in case
if(first==np) {first=rhs;} // of length==1.
if(last==np) {last=lhs;}
T result = np->datum;
delete np; length -= 1;
return result;
}

